{
  "version": "0.2",
  "phases": {
    "pre_build": {
      "commands": [
        "cd cdk",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk/cdk.out",
    "files": "**/*"
  }
}
